resource "google_compute_instance" "default" {
  name         = "test"
  machine_type = "n1-standard-1"
  zone         = "us-central1-a"
  boot_disk {
    initialize_params {
      image = "debian-cloud/debian-9"
    }
    disk_encryption_key_raw = "something"
  }

  service_account {
    scopes = ["https://www.googleapis.com/auth/cloud-platform", "compute-ro", "storage-ro"]
    # email = 
  }

  network_interface {
    network = "default"
    access_config {
      network_tier = "STANDARD"
    }
  }
  metadata = {
    block-project-ssh-keys = false
    serial-port-enable     = true
    enable-oslogin         = false
  }

  can_ip_forward   = true
  enable_flow_logs = false
  service_account {
    # Google recommends custom service accounts that have cloud-platform scope and permissions granted via IAM Roles.
    email  = "1234567890-compute@developer.gserviceaccount.com"
    scopes = ["cloud-platform"]
  }
  shielded_instance_config {
    enable_vtpm                 = false
    enable_integrity_monitoring = false
  }
}

resource "google_container_cluster" "fail" {
  enable_shielded_nodes = false #defaults to true for this
  node_config {
    shielded_instance_config {
      enable_integrity_monitoring = false
    }
  }
  #ip_allocation_policy = {}
  enable_legacy_abac = true
  logging_service    = "none"
  monitoring_service = "none"
  # master_authorized_networks_config {
  #   cidr_blocks {
  #     cidr_block="0.0.0.0/0"
  #   }
  # }
  master_auth {
    username = "paddy"
    password = "james"
    # client_certificate_config {
    #   issue_client_certificate = true
    # }
  }
  pod_security_policy_config {
    enabled = "false"
  }

}

resource "google_container_cluster" "fail_public" {
  node_config {
    shielded_instance_config {
      enable_integrity_monitoring = false
    }
  }

  enable_legacy_abac = true
  logging_service    = "none"
  monitoring_service = "none"
  master_authorized_networks_config {
    cidr_blocks {
      cidr_block = "0.0.0.0/0"
    }
  }
}

